import React, { Component } from 'react';
import Video from './Video';
import VideoInfo from './VideoInfo';
import './video-section.scss';
import VideoData from '../../data/video-details.json';

console.log(VideoData)

export default class VideoSection extends Component {
    constructor() {
        super();
        this.state = { videoId: VideoData[1].id}
    }

    setVideoId() {
        let newId = this.state.videoId
        this.setState({videoId: this.state.videoId})
        console.log(newId)
    }

    render() {
        console.log(this.state.videoId);
        const currentVideoIndex = VideoData.findIndex( element => element.id === this.state.videoId);
        console.log(currentVideoIndex)

        return (
            <section className="video">
                <Video mainVideo={VideoData[currentVideoIndex]}/>
                <VideoInfo mainVideo={VideoData[currentVideoIndex]}/>
            </section>
        )
    }
}

