import React, { Component} from 'react';
import VideoTitle from './VideoTitle';
import VideoDetails from './VideoDetails';
import VideoDescription from './VideoDescription';
import './video-info.scss';
import axios from 'axios';

export default class VideoInfo extends Component {

    state = {
        title: null,
        description: null,
        channel: null,
        likes: null,
        views: null,
        date: null,
    }

    fetchVideoInfo(id){
        axios.get('https://project-2-api.herokuapp.com/videos/'+ id + '?api_key=' + this.props.apiKey)
        .then(results => {

            const info = results.data;
            this.setState({
                title: info.title,
                description: info.description,
                channel: info.channel,
                likes: info.likes,
                views: info.views,
                date: info.timestamp,
            })
        })
    }

    componentDidMount(){
        console.log('video info did mount')
        this.fetchVideoInfo(this.props.currentVideo)
    }

    componentDidUpdate(prevProps, prevState,) {
        console.log('info updated')
        console.log(prevProps)
        console.log(prevState)
        console.log(this.props.routerProps)
        //console.log(this.state)
        //console.log('this.props.match.params.id:' + this.props.match.params.id)
        // if (this.props.match.params.id === this.state.videoId) {
        //     console.log('id matches')
        // }

    }

    render() {

        return (
            <div className="video-info">
                <h1 className="video-info_title">
                    {this.state.title}
                </h1>
                {/* <VideoTitle title={this.state.title} /> */}
                <VideoDetails
                    channel={this.state.channel}
                    likes={this.state.likes}
                    views={this.state.views}
                    date={this.state.date} />
                <p className="video-info_description">
                    {this.state.description}
                </p>
                {/* <VideoDescription description={this.state.description} /> */}
            </div>
        )
    }
}
