import React, { Component } from 'react';
//import VideoData from '../../data/video-details.json';
import NextVideosData from '../../data/videos.json';
import Video from '../2-Video/Video';
import BelowVideoSection from '../3-Below-Video/BelowVideoSection';
import axios from 'axios';
let apiKey = '2c4911e9-9ba1-4e47-9dc6-1054b805d4c7'


export default class HomePage extends Component {

    state = {
        VideoData: [],
        videoId: null,
        videoComments:[],
        //VideoData[0].id
    };

    fetchVideoList() {
        axios.get('https://project-2-api.herokuapp.com/videos?api_key=' + apiKey)
        .then(results => {
            console.log(results.data)
            this.setState({
                VideoData: results.data,
                videoId: results.data[0].id

            })
        });
    }

    getNewId = (id) => {
        this.setState({videoId: id})
    }

    componentDidMount() {
        console.log('component did mount');
        this.fetchVideoList();
        //this.fetchVideoComments();
    }

    fetchVideoComments(nextState) {
        console.log(this.state.videoId)
        axios.get('https://project-2-api.herokuapp.com/videos/' + this.state.videoId + '?api_key=' + apiKey)
        .then(results => {
            console.log(results.data.comments)
            // this.setState({
            //     videoComments: results.data.comments

            // })
        })
    }

    componentDidUpdate(prevProps, prevState,) {
        console.log('homepage updated')
        this.fetchVideoComments();
        //console.log(prevProps)
        //console.log(prevState)
        //console.log(this.state)
        //console.log('this.props.match.params.id:' + this.props.match.params.id)
        if (this.props.match.params.id === this.state.videoId) {
            console.log('id matches')
        }

    }

// componentDidUpdate()  is for when state gets updated
// componentWillUnmount()  still a little lost with this one

    render () {

    const currentVideoIndex = this.state.VideoData.findIndex(element => element.id === this.state.videoId);
    //console.log(this.props)
    console.log('rendering')
    //console.log(this.props.match.path)
    console.log(this.state.VideoData)

    if (!this.state.videoId) {
        return null
    }

    return (
        <div>
            <Video currentVideo={this.state.VideoData[currentVideoIndex]} />
            <BelowVideoSection
                currentVideo={this.state.VideoData[currentVideoIndex]}
                getNewId={this.getNewId}
                nextVideosList={NextVideosData}
                routerProps={this.props} />
        </div>
    )
}
}
