import React, { Component } from 'react';
import NextVideoData from '../../data/videos.json';
import NextVideos from './NextVideos';
import './next-videos.scss';

export default class NextVideoSection extends Component {

    constructor() {
        super();
        this.state = {videoId: NextVideoData[0].id}
    }

    removeCurrentVideo() {
        const currentVideoIndex = NextVideoData.findIndex( element => element.id === this.state.videoId)
        console.log(currentVideoIndex)

        const newVideoList = NextVideoData.splice(currentVideoIndex,1);
        console.log(newVideoList)
    }


    render() {
        console.log(this.state.videoId)

        const currentVideoIndex = NextVideoData.findIndex( element => element.id === this.state.videoId)
        console.log(currentVideoIndex)

        const newVideoList = NextVideoData.splice(currentVideoIndex);
        console.log(newVideoList)

        const videoDataJsx = NextVideoData.map((element) => {
            return <NextVideos videoData ={element} key={element.id} />;
        })

        return (
            <section className="next-videos">
                <h2>NEXT VIDEOS</h2>
                {videoDataJsx}
            </section>
        )
    }
}
