import React, { Component } from 'react';
import './upload-video-section.scss';
import UploadThumbnail from '../../assets/images/Upload-video-preview.jpg';
import PublishIcon from '../../assets/images/icons/publish.svg';
import { Input } from 'postcss';
import { Link } from 'react-router-dom';

export default class VideoUploadPage extends Component {
    state = {
        newVideo: {
            title: "",
            description: "",
        }
        //newVideoTitle: "",
        //newVideoDesc: "",
    };

    //manages upload form title state change
    handleTitleInputChange = (text) => {
        this.setState({
            newVideo: {
                title: text,
            }
        })
    };

    //manages upload form description state change
    handleDescInputChange = (text) => {
        this.setState({
            newVideo: {
                description: text,
            }
        })
    };

    //upload form submisison
    addNewVideo = (e) => {
        e.preventDefault();

        console.log(this.state.newVideo)
        // axios.post('http://localhost:8080/videos?api_key=' + apiKey, {
        //     comment: this.state.newComment,
        //   })
        // .then(data => {
        //   this.setState({
        //     videoInfo: data.data,
        //   })
        // })
    };

    componentWillUnmount(){
        window.alert("Upload Successful");
    }

    render(){

        return (
            <div className="uploadvideo-seciton">
                <h1 className="uploadvideo-title">Upload Video</h1>
                <h2 className="uploadvideo-subtitle">Video Thumbnail</h2>
                <div className="uploadvideo-form_container">
                <img src={UploadThumbnail} className="uploadvideo-form_thumbnail"/>
                <form className="uploadvideo-form">
                    <div className="uploadvideo-form_field">
                        <label form="video-title"><h2>Title Your Video</h2></label>
                        <input type="text" name="video-title" id="video-title" placeholder="Add a title to your video"
                            onChange = {(e) => this.handleTitleInputChange(e.target.value)}></input>
                    </div>
                    <div className="uploadvideo-form_field">
                        <label form="video-description"><h2>Add a Video Desctiption</h2></label>
                        <textarea name="video-description" id="video-description" placeholder="Add a description to your video"
                            onChange = {(e) => this.handleDescInputChange(e.target.value)}></textarea>
                    </div>
                    <Link to="/" className="header-button_link">
                        <button type="submit" className="uploadvideo-form_button">
                            <img src={PublishIcon} alt="uploadvideo-icon" />
                            <p>Publish</p>
                        </button>
                    </Link>
                </form>
            </div>
            </div>
        )
    }
}
